import React, { useEffect, useState } from 'react';

import NavBar from '../../Components/NavBar';

import style from './style.module.css';
import userImg from '../../assets/userprofile.svg';
import { Modal } from 'react-bootstrap';
import Footer from '../../Components/Footer';

import api from '../../service/api';

import password from '../../assets/password.svg';
import username from '../../assets/username.svg';

const UserProfile = () => {
  const [showChangeP, setShowChangeP] = useState(false);
  const [showChangeU, setShowChangeU] = useState(false);
  const [currentP, setCurrentP] = useState('');
  const [passw, setPassw] = useState('');
  const [newUsername, setNewUsername] = useState('');
  const [user, setUser] = useState({});

  const [confirmPassw, setConfirmPassw] = useState('');

  const handleCloseChangeU = () => setShowChangeU(false);
  const handleShowChangeU = () => setShowChangeU(true);
  //Show and hidde changeP modal
  const handleCloseChangeP = () => setShowChangeP(false);
  const handleShowChangeP = () => setShowChangeP(true);
  const changePassword = async (event) => {
    event.preventDefault();

    // Check if the new passwords match
    if (confirmPassw !== passw) {
      alert('The passwords do not match. Please try again.');
      return;
    }

    try {
      const response = await api.post('/user/change/password', {
        email: user.email,
        password: passw,
        oldPassword: currentP,
      });

      alert(response.data); // Assuming response.data contains a success message
      window.location.reload(); // Refresh the page after a successful password change

    } catch (error) {
      if (error.response) {
        // Server responded with an error
        alert(error.response.data.error || 'An error occurred while changing the password.');
      } else if (error.request) {
        // No response was received
        console.log('Error request:', error.request);
        alert('No response received from the server.');
      } else {
        // Other errors
        console.log('Error:', error.message);
        alert('An error occurred: ' + error.message);
      }
    }
  };

  const changeUsername = async (event) => {
    event.preventDefault();

    try {
      const response = await api.post('/user/change/username', {
        username: newUsername,
        email: user.email,
        password: passw,
      });

      alert(response.data); // Assuming response.data contains a success message
      window.location.reload(); // Refresh the page after a successful username change

    } catch (error) {
      if (error.response) {
        // Server responded with an error
        alert(error.response.data.error || 'An error occurred while changing the username.');
      } else if (error.request) {
        // No response was received
        console.log('Error request:', error.request);
        alert('No response received from the server.');
      } else {
        // Other errors
        console.log('Error:', error.message);
        alert('An error occurred: ' + error.message);
      }
    }
  };

  //get user information
  const getUser = async () => {
    const token = localStorage.getItem('token');

    try {
      const response = await api.get('/user', {
        headers: { Authorization: `Bearer ${token}` }
      });

      // If the user is successfully registered, they can be logged in
      setUser(response.data);

    } catch (error) {
      if (error.response) {
        // Handle errors returned from the server
        console.error('Error response:', error.response.data);
      } else if (error.request) {
        // Handle errors where no response was received
        console.error('Error request:', error.request);
      } else {
        // Handle other errors
        console.error('Error message:', error.message);
      }
    }
  };

  useEffect(() => {
    getUser();
  }, []);

  return (
    <>
      <div className={style.body}>
        <NavBar scrolled={true}></NavBar>

        <div className={style.container}>
          <div>
            <img src={userImg} className={style.img}></img>
          </div>
          <div className={style.imgC}>
            <p>
              Username: <strong className={style.username}>{user.name}</strong>
            </p>
          </div>
          <div className={style.changes}>
            <div className={style.changeP} onClick={handleShowChangeP}>
              <img src={password}></img>
              <p>Change Password</p>
            </div>
            <div className={style.changeD} onClick={handleShowChangeU}>
              <img src={username}></img>
              <p>Change Username</p>
            </div>
          </div>
        </div>
      </div>

      {/* Change Password */}
      <Modal show={showChangeP} onHide={handleCloseChangeP}>
        <Modal.Header closeButton>
          <Modal.Title>Change Password</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form className={style.signUpForm} onSubmit={changePassword}>
            <div id={style.errorMessage2}></div>
            <label htmlFor="currentP">Current Password*</label>
            <input
              type={'password'}
              required
              id="currentP"
              onChange={(event) => setCurrentP(event.target.value)}
            ></input>

            <label htmlFor="password">New Password*</label>
            <input
              type={'password'}
              id="password"
              required
              onChange={(event) => setPassw(event.target.value)}
            ></input>

            <label htmlFor="password">Confirm Password*</label>
            <input
              type={'password'}
              id="password"
              required
              onChange={(event) => setConfirmPassw(event.target.value)}
            ></input>

            <button type="submit">Change Password</button>
          </form>
        </Modal.Body>
      </Modal>

      {/* Change Username */}
      <Modal show={showChangeU} onHide={handleCloseChangeU}>
        <Modal.Header closeButton>
          <Modal.Title>Change Username</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form className={style.signUpForm} onSubmit={changeUsername}>
            <div id={style.errorMessage2}></div>
            <label htmlFor="username">New Username*</label>
            <input
              type={'text'}
              required
              id="username"
              onChange={(event) => setNewUsername(event.target.value)}
            ></input>

            <label htmlFor="password">Password*</label>
            <input
              type={'password'}
              id="password"
              required
              onChange={(event) => setPassw(event.target.value)}
            ></input>

            <button type="submit">Change Usernaaaame</button>
          </form>
        </Modal.Body>
      </Modal>
      <Footer></Footer>
    </>
  );
}

export default UserProfile;
