import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import searchOnYoutube from 'youtube-search';
import style from './style.module.css';
import { Icon } from '@iconify/react';
import api from '../../service/api';
import { DebounceInput } from 'react-debounce-input';
import io from 'socket.io-client';
import { socketIoUrl, ytApiKey } from '../../config';

const SearchBar = () => {
  const [name, setName] = useState('');
  const [video, setVideo] = useState([]);
  const [userId, setUserId] = useState(null);
  const history = useNavigate();

  // Function to check if the song is already classified
  const isTheSongAlreadyClassified = async (id) => {
    try {
      const response = await api.get(`/song/${id}`);
      return response.data ? 1 : 0;
    } catch (error) {
      console.error(error);
      return 0;
    }
  };

  // Function to get the user ID
  const getUserID = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await api.get('/user', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUserId(response.data.email);
    } catch (error) {
      setUserId(null);
    }
  };

  // Function to classify a song
  const classifySong = (song_id) => {
    const socket = io(socketIoUrl, {
      transports: ['websocket', 'polling', 'flashsocket'],
      query: { song_id },
    });

    socket.on('connect', async () => {
      try {
        const response = await api.post(`/classification/${song_id}/user/${userId}`);
        alert(response.data);
        history(`/queue`);
      } catch (error) {
        alert(error.response ? error.response.data : error.message);
      } finally {
        socket.disconnect(); // Close the socket connection
      }
    });
  };

  // Function to search for videos on YouTube
  useEffect(() => {
    const search = async (query) => {
      const opts = {
        maxResults: 4,
        key: ytApiKey,
        type: 'video',
        videoCategoryId: '10', // Category ID for music
      };

      searchOnYoutube(query, opts, async (err, results) => {
        if (err) {
          console.error('YouTube Search Error:', err);
          return;
        }

        console.log('YouTube Search Results:', results); // Log results

        const videos = await Promise.all(
          results.map(async (video) => ({
            id: video.id,
            thumbnail: video.thumbnails.default.url,
            title: video.title,
            isTheSongAlreadyClassified: await isTheSongAlreadyClassified(video.id),
          }))
        );
        setVideo(videos);
      });
    };

    if (name.trim()) {
      search(name);
    }
  }, [name]);

  useEffect(() => {
    getUserID();
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    history(`/browse/?name=${name}`);
  };

  return (
    <>
      <div id={style.SearchBar} className={video.length > 0 ? style.active : style.noactive}>
        <div className={style.filter}>
          <form className={style.form} onSubmit={handleSubmit}>
            <DebounceInput
              minLength={3}
              debounceTimeout={500}
              type="text"
              placeholder="Search for a song or insert a YouTube link..."
              onChange={(event) => setName(event.target.value)}
              required
            />
            <button type="submit">
              <Icon icon="fe:search" height="36px" />
            </button>
          </form>
        </div>
      </div>

      <div id={style.searchresults} className={video.length > 0 ? style.active : style.noactive}>
        {video.map((video) => (
          <div key={video.id} onClick={() => history(`/music/${video.id}`)}>
            <div className={style.imageResult}>
              <img src={video.thumbnail} alt={`${video.title} video thumbnail`} />
            </div>
            <div>
              <div className={style.descriptionResult}>
                <p>{video.title}</p>
              </div>
              {video.isTheSongAlreadyClassified === 1 ? (
                <div className={style.isAlreadyClassified}>
                  <p>This music is already classified</p>
                  <a href={`/music/${video.id}`}>See More</a>
                </div>
              ) : (
                <div className={style.classifyButton}>
                  <button onClick={() => classifySong(video.id)}>Classify</button>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default SearchBar;
