import React, { useEffect, useState } from 'react';

import NavBar from '../../Components/NavBar';

import indexImage from '../../Assets/index.jpg';

import style from './style.module.css';

import classNames from 'classnames';


const Home = () => {

  return (
    <>
      {/*Loading Bar*/}
      <div className="super_container">
        <NavBar scrolled={false} active={1}></NavBar>

        <div className={style.home}>
          <div
            className={style.background_image}
            style={{ backgroundImage: 'url(' + indexImage  + ')' }}
          ></div>
          {/*Title to help the user use the searchbar*/}
          <div className={style.home_container}>
            <div className={style.home_title}>
              <h1>
                Welcome to my {' '}
                <span className={style.highlight_word}>Portfolio</span>
              </h1>
            </div>
          </div>
        </div>

        <div className={style.intro}>
          <div className="container">
            <div className="row">
              <div className="col">
                <div className={classNames(style.intro_content, 'text-center')}>
                  <div className={classNames(style.section_title, 'text-center')}>
                    <h1>
                      About MER<span>maid</span>
                    </h1>
                  </div>
                  <div className={classNames(style.intro_text, 'text-center')}>
                    <p>
                      MERmaid is a Music Emotion Recognition (MER) application from the{' '}
                      <a href="http://mir.dei.uc.pt/" target="_blank" rel="noreferrer">
                        MIR.CISUC team
                      </a>
                      , part of the MERGE research project (
                      <a href="https://doi.org/10.54499/PTDC/CCI-COM/3171/2021" target="_blank" rel="noreferrer">
                        PTDC/CCI-COM/3171/2021
                      </a>
                      ). The system has been developed over the last years in a collaboration
                      between{' '}
                      <a href="https://www.cisuc.uc.pt/" target="_blank" rel="noreferrer">
                        CISUC
                      </a>{' '}
                      (emotion recognition) and{' '}
                      <a href="http://ci2.ipt.pt/" target="_blank" rel="noreferrer">
                        Ci2.ipt
                      </a>{' '}
                      (software engineering part), with the purpose of illustrating the advances on
                      this research topic, which usually stay buried in academic papers and do not
                      reach the general public.
                    </p>
                  </div>
                  <div className={classNames(style.button_fill, style.intro_button)}>
                    <a href="about">read more</a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
