/* eslint-disable react/prop-types */

import React, { useEffect, useState } from 'react';
import style from './style.module.css';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import classnames from 'classnames';
import { Modal } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const NavBar = (props) => {
  const [scrolled, setScrolled] = useState(false);
  const turnNavBarBlack = () => setScrolled(props.scrolled);
  const [activeMenu, setActiveMenu] = useState(false);

  const [showRegister, setShowRegister] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [showConfirmEmail, setShowConfirmEmail] = useState(false);
  const [showLogout, setShowLogout] = useState(false);
  const [showResetPassword, setShowResetPassword] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  //User Input variables
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [cpassword, setCPassword] = useState('');
  const [name, setName] = useState('');
  //Token of the user
  const [user, setUser] = useState('');
  //Track first reload of the page
  const [needsAuth, setNeedsAuth] = useState(false);
  //navigation
  const navigation = useNavigate();

  const [emailReset, setEmailReset] = useState('');

  // Activate the menu to create an animation as soon as the user clicks on the hamburger menu icon
  const hamburguerMenu = () => {
    setActiveMenu(prevState => !prevState);
  };

  // Turn the header black on scrolling
  const turnBlackOnScrolling = () => {
    setScrolled(window.scrollY > 0);
  };

  // Turn the NavBar black every time the user scrolls
  useEffect(() => {
    const handleScroll = () => turnBlackOnScrolling();

    window.addEventListener('scroll', handleScroll);

    if (props.scrolled) {
      turnNavBarBlack();
    }

    // Cleanup event listener on unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <div className="top-loading-bar" id="loading-bar"></div>
      <header
        className={
          scrolled
            ? classnames(style.header, 'trans_400', style.scrolled)
            : classnames(style.header, 'trans_400')
        }
      >
        <div className={style.logo}>
          <a href="/">
            <span>MER</span>maid
          </a>
        </div>
        <div className="container">
          <div className="row">
            <div className="col">
              <div
                className={classnames(
                  style.header_content,
                  'd-flex flex-row align-items-center justify-content-start trans_400'
                )}
              >
                <nav className={style.main_nav}>
                  <ul className="d-flex flex-row align-items-start justify-content-start">
                    <li>
                      <a href="/" className={props.active == 1 ? style.active : ''}>
                        Home
                      </a>
                    </li>
                    <li>
                      <a href="/queue" className={props.active == 2 ? style.active : ''}>
                        Queue
                      </a>
                    </li>
                    <li>
                      <a href="/browse" className={props.active == 3 ? style.active : ''}>
                        Browse
                      </a>
                    </li>
                    <li>
                      <a href="/about" className={props.active == 4 ? style.active : ''}>
                        About
                      </a>
                    </li>
                  </ul>
                </nav>
              </div>
            </div>
          </div>
        </div>

        {localStorage.getItem('token') ? (
          <div
            className={classnames(
              style.header_right,
              'd-flex flex-row align-items-start justify-content-start'
            )}
          >
            <div className={classnames(style.submit, style.signUp)}>
              <a onClick={handleShowLogout}>LogOut</a>
            </div>
            <div className={classnames('dropdown', style.userS)}>
              <button
                className={classnames('btn btn-secondary dropdown-toggle', style.d)}
                type="button"
                id="dropdownMenuButton"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
              >
                {user.name}
              </button>
              <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">

                <>
                  <a className="dropdown-item" onClick={handleShowRegister} href="#">
                    Create User
                  </a>
                  <a className="dropdown-item" href="/manage/users">
                    Manage Users
                  </a>
                </>
                ) : (
                <>
                  <a className="dropdown-item" href="/userprofile">
                    View Profile
                  </a>
                </>

              </div>
            </div>
            <div className={style.hamburger} onClick={hamburguerMenu}>
              <FontAwesomeIcon icon={faBars} aria-hidden="true" size="2x" inverse />
            </div>
          </div>
        ) : (
          <div
            className={classnames(
              style.header_right,
              'd-flex flex-row align-items-start justify-content-start'
            )}
          >
            <div className={classnames(style.submit, style.signUp)}>
              <a onClick={handleShowRegister}>SignUp</a>
            </div>
            <div className={classnames(style.submit, style.login)}>
              <a onClick={handleShowLogin}>Login</a>
            </div>
            <div className={style.hamburger} onClick={hamburguerMenu}>
              <FontAwesomeIcon icon={faBars} aria-hidden="true" size="2x" inverse />
            </div>
          </div>
        )}
      </header>

      <div className={activeMenu ? classnames(style.menu, style.active) : style.menu}>
        <div
          className={classnames(
            style.menu_content,
            'd-flex flex-column align-items-end justify-content-start'
          )}
        >
          <ul className={classnames(style.menu_nav_list, 'text-right')}>
            <li>
              <a href="/" className={props.active == 1 ? style.active : ''}>
                Home
              </a>
            </li>
            <li>
              <a href="/queue" className={props.active == 2 ? style.active : ''}>
                Queue
              </a>
            </li>
            <li>
              <a href="/browse" className={props.active == 3 ? style.active : ''}>
                Browse
              </a>
            </li>
            <li>
              <a href="/about" className={props.active == 4 ? style.active : ''}>
                About
              </a>
            </li>
          </ul>
        </div>
      </div>
    </>
  );
}

export default NavBar;
