import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Popover } from 'bootstrap/dist/js/bootstrap.esm.min.js';

import { Parallax } from 'react-parallax';
import CountUp from 'react-countup';
import milestonesImage from '../../assets/milestones.jpg';
import { faClock, faVideo } from '@fortawesome/free-solid-svg-icons';

import React, { useEffect, useState } from 'react';

import api from '../../service/api';

import style from './style.module.css';

import classNames from 'classnames';

const FunFacts = () => {
  const [streamedMinutes, setStreamedMinutes] = useState(0);
  const [analysedVideos, setAnalysedVideos] = useState(0);

  const getStreamedMinutes = async () => {
    try {
      const response = await api.get('/song/get/streamedminutes');
      setStreamedMinutes(response.data);
    } catch (error) {
      handleApiError(error);
    }
  };

  const getAnalysedVideos = async () => {
    try {
      const response = await api.get('/song/get/analysed/videos');
      setAnalysedVideos(response.data);
    } catch (error) {
      handleApiError(error);
    }
  };

  const addPopoverElements = () => {
    Array.from(document.querySelectorAll('button[data-bs-toggle="popover"]')).forEach(
      (popoverNode) =>
        new Popover(popoverNode, {
          trigger: 'focus',
        })
    );
  };

  const handleApiError = (error) => {
    if (error.response) {
      console.log('Error response data:', error.response.data);
      console.log('Error response status:', error.response.status);
      console.log('Error response headers:', error.response.headers);
    } else if (error.request) {
      console.log('Error request:', error.request);
    } else {
      console.log('Error message:', error.message);
    }
  };

  useEffect(() => {
    getStreamedMinutes();
    getAnalysedVideos();
    addPopoverElements();
  }, []);

  return (
    <>
      <Parallax
        className="parallax_background parallax-window"
        bgImage={milestonesImage}
        speed={0.8}
      >
        <div className={classNames('container', style.milestones)}>
          <div className="row">
            <div className="col">
              <div className={classNames(style.section_title, style.light, 'text-center')}>
                <h1>Fun Facts</h1>
              </div>
            </div>
          </div>

          <div className={classNames('row', style.milestones_row)}>
            <div className="col-xl-6 col-md-12">
              <div className={classNames(style.milestone, 'text-center')}>
                <div className={classNames(style.milestone_content, 'ml-auto mr-auto')}>
                  <CountUp className={style.milestone_counter_StreamedMinutes} end={streamedMinutes}></CountUp>
                  <div className={style.milestone_icon}>
                    <FontAwesomeIcon icon={faClock} size="6x" />
                  </div>
                </div>
                <div className={style.milestone_title}>Streamed Minutes</div>
              </div>
            </div>
            <div className="col-xl-6 col-md-12">
              <div className={classNames(style.milestone, 'text-center')}>
                <div className={classNames(style.milestone_content, 'ml-auto mr-auto')}>
                  <CountUp className={style.milestone_counter_AnalyzedVideos} end={analysedVideos}></CountUp>
                  <div className={style.milestone_icon}>
                    <FontAwesomeIcon icon={faVideo} size="6x" />
                  </div>
                </div>
                <div className={style.milestone_title}>Analyzed Videos</div>
              </div>
            </div>
          </div>
        </div>
      </Parallax>
    </>
  );
}
export default FunFacts;
