/* eslint-disable react/prop-types */

import React, { useEffect, useState } from 'react';
import style from './style.module.css';

import loading from '../../assets/loading.svg';
import classNames from 'classnames';

const ProcessingSong = (props) => {
  const [state, setState] = useState('');
  const [percent, setPercent] = useState(0);

  const song_id = props.song.external_id;

  useEffect(() => {
    const interval = setInterval(() => {
      const songs = JSON.parse(localStorage.getItem('songs')) || [];
      const currentSong = songs.find((s) => song_id === s.id);
      if (currentSong) {
        setPercent(currentSong.progress);
        setState(currentSong.state);
      }
    }, 100);

    return () => {
      clearInterval(interval);
    };
  }, [song_id]);

  return (
    <>
      {props.song.status !== 'processed' && percent != 100 ? (
        <div
          className={classNames(
            style.blog_post,
            'd-flex flex-md-row flex-column align-items-start justify-content-start'
          )}
        >
          <div className={style.blog_post_image}>
            <iframe
              className={style.youtubeIframe}
              width="485"
              height="273"
              src={`https://www.youtube-nocookie.com/embed/${props.song.external_id}`}
              title="YouTube video player"
              style={{ border: 'none' }}
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>

          </div>
          <div className={style.blog_post_content}>
            <div className={style.blog_post_title}>{props.song.title}</div>
            <div className={style.processing}>
              <div>
                <img src={loading}></img>
                <p>{percent}%</p>
              </div>
              <p>{state}...</p>
            </div>
          </div>
        </div>
      ) : (
        <div
          className={classNames(
            style.blog_post,
            'd-flex flex-md-row flex-column align-items-start justify-content-start'
          )}
        >
          <div className={style.blog_post_image}>
            <iframe
              width="485"
              height="273"
              src={`https://www.youtube-nocookie.com/embed/${props.song.external_id}`}
              title="YouTube video player"
              style={{ border: 'none' }}
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowfullscreen
            ></iframe>
          </div>
          <div className={style.blog_post_content}>
            <div className={style.blog_post_title}>
              <a href={`/music/${props.song.external_id}`}>{props.song.title}</a>
            </div>
            <div className={style.blog_post_author}>
              {props.song.artist} on {new Date(props.song.date).getDay()}/
              {new Date(props.song.date).getMonth()}/{new Date(props.song.date).getFullYear()}
              <br />
              Added to the Queue on {new Date(props.song.createdAt).getDay()}/
              {new Date(props.song.createdAt).getMonth()}/
              {new Date(props.song.createdAt).getFullYear()}
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ProcessingSong;
