import React, { useEffect, useState } from 'react';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Parallax } from 'react-parallax';
import Footer from '../../Components/Footer';
import NavBar from '../../Components/NavBar';

import style from './style.module.css';
import classNames from 'classnames';

import browseImage from '../../assets/browse.jpg';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { faCircleXmark } from '@fortawesome/free-solid-svg-icons';

import api from '../../service/api';
import { useLocation } from 'react-router-dom';

import notfound from '../../assets/notfound.svg';
import Pagination from '../../Components/Pagination';
import { Modal } from 'react-bootstrap';
import loading from '../../assets/loading.svg';

const Browse = () => {
  const location = useLocation();
  const queryParameters = new URLSearchParams(location.search);
  var name = queryParameters.get('name');
  var emotion = queryParameters.get('emotion');

  const [songs, setSongs] = useState([]);
  const [search, setSearch] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  const [showConfirmDelete, setShowConfirmDelete] = useState(false);
  //handle logout
  const handleCloseDelete = () => setShowConfirmDelete(false);
  function handleShowDelete(externalid) {
    setShowConfirmDelete(true);
    setIdSong(externalid);
  }
  //Variables for Pagination
  //This variables are responsible to mantain the pagination of the songs on the website
  const [idSong, setIdSong] = useState('');

  const [currentPage, setCurrentPage] = useState(1); //By default we are always on page 1
  const [songsPerPage] = useState(3);
  const [isAdmin, setIsAdmin] = useState(false);

  const indexOfLastSong = currentPage * songsPerPage;
  const indexOfFirstSong = indexOfLastSong - songsPerPage;
  const currentSongs = songs.slice(indexOfFirstSong, indexOfLastSong);

  //Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  //This method of pagination was originally create by: https://github.com/bradtraversy/simple_react_pagination/blob/master/src/App.js

  const getSongs = async () => {
    try {
      const response = await api.get('/song');
      setSongs(response.data);
    } catch (error) {
      if (error.response) {
        console.error('Error response:', error.response.data);
      } else if (error.request) {
        console.error('Error request:', error.request);
      } else {
        console.error('Error message:', error.message);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const getSongsbyName = async (name) => {
    try {
      const response = await api.get(`/song/name/${name}`);
      setSongs(response.data);
    } catch (error) {
      if (error.response) {
        console.error('Error response:', error.response.data);
      } else if (error.request) {
        console.error('Error request:', error.request);
      } else {
        console.error('Error message:', error.message);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const getSongsByEmotion = async (emotion) => {
    try {
      const response = await api.get(`/song/emotion/${emotion}`);
      setSongs(response.data);
    } catch (error) {
      if (error.response) {
        console.error('Error response:', error.response.data);
      } else if (error.request) {
        console.error('Error request:', error.request);
      } else {
        console.error('Error message:', error.message);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const getSongsbyAll = async (emotion, name) => {
    try {
      const response = await api.get(`/song/name/${name}/emotion/${emotion}`);
      setSongs(response.data);
    } catch (error) {
      if (error.response) {
        console.error('Error response:', error.response.data);
      } else if (error.request) {
        console.error('Error request:', error.request);
      } else {
        console.error('Error message:', error.message);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const isUserAdmin = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await api.get('/user', {
        headers: { Authorization: `Bearer ${token}` }
      });
      if (response.data.admin) {
        setIsAdmin(true);
      }
    } catch (error) {
      if (error.response) {
        // Handle error
      } else if (error.request) {
        console.log(error.request);
      } else {
        console.log('Error', error.message);
      }
    }
  };

  const deleteSong = async (id) => {
    try {
      await api.delete(`/song/delete/${id}`);
      window.location.reload();
    } catch (error) {
      if (error.response) {
        // Handle error
      } else if (error.request) {
        console.log(error.request);
      } else {
        console.log('Error', error.message);
      }
    }
  };

  const updateUrlParams = (key, value) => {
    const searchParams = new URLSearchParams(window.location.search);
    searchParams.set(key, value);
    const newUrl = window.location.pathname + '?' + searchParams.toString();
    window.location = newUrl;
  };

  const deleteUrlParams = (key) => {
    const url = new URL(window.location.href);
    url.searchParams.delete(key);
    window.location = url.toString();
  };

  const handleSubmit = (event) => {
    if (search.length === 0) {
      deleteUrlParams('name');
    } else {
      event.preventDefault();
      updateUrlParams('name', search);
    }
  };

  const onlyOneCheckBox = (id) => {
    const input = document.getElementById(id).checked;
    document.getElementById('Happy').checked = false;
    document.getElementById('Calm').checked = false;
    document.getElementById('Sad').checked = false;
    document.getElementById('Tense').checked = false;

    if (input) {
      document.getElementById(id).checked = true;
      updateUrlParams('emotion', id);
    } else {
      document.getElementById(id).checked = false;
      deleteUrlParams('emotion');
    }
  };

  const amIChecked = (id) => id === emotion;

  useEffect(() => {
    if (name == null && emotion == null) {
      getSongs();
    } else if (name != null && emotion == null) {
      getSongsbyName(name);
    } else if (name == null && emotion != null) {
      getSongsByEmotion(emotion);
    } else if (name != null && emotion != null) {
      getSongsbyAll(emotion, name);
    }
    isUserAdmin();
  }, [name, emotion]);

  if (isLoading) {
    return (
      <div>
        <NavBar scrolled={true}></NavBar>
        <div className={style.Loading}>
          <img src={loading} alt="Loading" />
        </div>
      </div>
    );
  }

  return (
    <>
      {/*Loading Bar*/}
      <div className="super_container">
        <NavBar active={3}></NavBar>
        <Parallax className="parallax_background parallax-window" speed={0.8} bgImage={browseImage}>
          <div className={style.home}>
            <div
              className={classNames(
                style.home_container,
                'd-flex flex-column align-items-center justify-content-center'
              )}>
              <div className={style.home_content}>
                <div className={style.home_title}>
                  <h1>browse</h1>
                </div>
              </div>
            </div>
          </div>
        </Parallax>

        <div className={style.blog}>
          <div className="container">
            <div className="row">
              <div className={classNames('col-lg-3 order-lg-1 order-2', style.sidebar_col)}>
                <div className={style.sidebar}>
                  <div className={style.sidebar_search}>
                    <div className={style.sidebar_title}>Search</div>
                    {/*SearchBar */}
                    <form
                      className={style.search_form}
                      id="search_form"
                      action="/browse/"
                      onSubmit={handleSubmit}>
                      <input
                        type="text"
                        className={style.search_input}
                        placeholder="Search"
                        onChange={(event) => setSearch(event.target.value)}
                      />
                      <button className={style.search_button} type="submit">
                        <FontAwesomeIcon icon={faSearch} />
                      </button>
                    </form>
                  </div>

                  <div className={style.sidebar_tags}>
                    <div className={style.sidebar_title}>Feeling</div>
                    <div className={style.tags}>
                      <form>
                        <div className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            value=""
                            id="Happy"
                            onChange={() => onlyOneCheckBox('Happy')}
                            checked={amIChecked('Happy')}></input>
                          <label className="form-check-label" htmlFor="Happy">
                            Happy
                          </label>
                        </div>

                        <div className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            value=""
                            id="Calm"
                            onChange={() => onlyOneCheckBox('Calm')}
                            checked={amIChecked('Calm')}></input>
                          <label className="form-check-label" htmlFor="Calm">
                            Calm
                          </label>
                        </div>

                        <div className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            value=""
                            id="Sad"
                            onChange={() => onlyOneCheckBox('Sad')}
                            checked={amIChecked('Sad')}></input>
                          <label className="form-check-label" htmlFor="Sad">
                            Sad
                          </label>
                        </div>

                        <div className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            value=""
                            id="Tense"
                            onChange={() => onlyOneCheckBox('Tense')}
                            checked={amIChecked('Tense')}></input>
                          <label className="form-check-label" htmlFor="Tense">
                            Tense
                          </label>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>

              <div className={classNames('col-lg-9', style.blog_col, 'order-lg-2 order-1')}>
                <div id={style.SearchResults} className={name ? style.active : style.noactive}>
                  <a>Results for: &quot;{name}&quot;</a>
                  <button
                    id={style.ResetButton}
                    className={name ? style.active : style.noactive}
                    onClick={() => {
                      deleteUrlParams('name')
                      setSearch('')
                    }}
                  >
                   <FontAwesomeIcon icon={faCircleXmark} />
                  </button>
                </div>

                <div className={style.blog_posts}>
                  {songs.length == 0 ? (
                    <div className={style.notFound}>
                      <div>
                        <img src={notfound}></img>
                      </div>
                      <div>
                        <h1>Results Not Found</h1>
                      </div>
                      <div>
                        We regret that your search returned no results. We advise you to change your
                        search terms or come back later. We appreciate you using our website.
                      </div>
                    </div>
                  ) : (
                    currentSongs.map((song) => {
                      const date = new Date(song.date);
                      const formattedDate = `${date.getDate()}/${date.getMonth() + 1}/${date.getFullYear()}`;
                      const hrefURL = `?emotion=${song.general_classification}`;
                      const hrefURL_music = `/music/${song.external_id}`;
                      return (
                        /* @panda: this should be a component and have the key=xx there? */
                        <div
                          key={song.id}
                          className={classNames(
                            style.blog_post,
                            'd-flex flex-md-row flex-column align-items-start justify-content-start'
                          )}>
                          <div className={style.blog_post_image}>
                            <img src={song.thumbnailHQ} alt={song.title} />
                            <div className={style.blog_post_classification}>
                              <a href={hrefURL}>{song.general_classification}</a>
                            </div>
                          </div>
                          <div className={style.blog_post_content}>
                            <div className={style.blog_post_title}>
                              <a href={hrefURL_music}>{song.title}</a>
                            </div>
                            <div className={style.blog_post_author}>
                              {song.artist} on {formattedDate}
                            </div>
                            <div className={style.blog_post_link}>
                              <a href={hrefURL_music}>See More</a>
                            </div>
                            <div>
                              {isAdmin ? (
                                <>
                                  <button
                                    className={style.delete_button}
                                    onClick={() => handleShowDelete(song.external_id)}>
                                    Delete Song
                                  </button>
                                </>
                              ) : (
                                <></>
                              )}
                            </div>
                          </div>
                        </div>
                      );
                    })
                  )}
                </div>
              </div>
            </div>

            {songs.length == 0 ? (
              <div></div>
            ) : (
              <div>
                <Pagination
                  songsPerPage={songsPerPage}
                  totalSongs={songs.length}
                  paginate={paginate}
                  currentPage={currentPage}
                />
              </div>
            )}
          </div>
        </div>
        <Footer></Footer>
      </div>

      <Modal show={showConfirmDelete} onHide={handleCloseDelete}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Delete</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <div className="modal-body">Are you sure you want to delete this song ?</div>
            <div className="modal-footer">
              <button
                type="button"
                className={style.LogOutButton}
                onClick={() => deleteSong(idSong)}>
                Delete
              </button>
              <button
                type="button"
                className={style.CancelLogOutButton}
                onClick={handleCloseDelete}>
                Close
              </button>
            </div>
          </div>
        </Modal.Body>
      </Modal>
    </>
  );
};

export default Browse;
