/* eslint-disable react/prop-types */

import React, { useEffect, useState } from 'react';
import style from './style.module.css';
import logo from '../../assets/play.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import classnames from 'classnames';
import { Modal } from 'react-bootstrap';
import api from '../../service/api';
import { useNavigate } from 'react-router-dom';
import emailLogo from '../../assets/emailLogo.svg';

const NavBar = (props) => {
  const [scrolled, setScrolled] = useState(false);
  const turnNavBarBlack = () => setScrolled(props.scrolled);
  const [activeMenu, setActiveMenu] = useState(false);

  const [showRegister, setShowRegister] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [showConfirmEmail, setShowConfirmEmail] = useState(false);
  const [showLogout, setShowLogout] = useState(false);
  const [showResetPassword, setShowResetPassword] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  //User Input variables
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [cpassword, setCPassword] = useState('');
  const [name, setName] = useState('');
  //Token of the user
  const [user, setUser] = useState('');
  //Track first reload of the page
  const [needsAuth, setNeedsAuth] = useState(false);
  //navigation
  const navigation = useNavigate();

  const [emailReset, setEmailReset] = useState('');

  // Activate the menu to create an animation as soon as the user clicks on the hamburger menu icon
  const hamburguerMenu = () => {
    setActiveMenu(prevState => !prevState);
  };

  // Turn the header black on scrolling
  const turnBlackOnScrolling = () => {
    setScrolled(window.scrollY > 0);
  };

  //Show and hidde register modal
  const handleCloseRegister = () => setShowRegister(false);
  const handleShowRegister = () => setShowRegister(true);

  //Show and hidde login modal
  const handleCloseLogin = () => setShowLogin(false);
  const handleShowLogin = () => setShowLogin(true);

  //handle confirm email modal
  const handleShowConfirmEmail = () => setShowConfirmEmail(true);
  const handleCloseConfirmEmail = () => setShowConfirmEmail(false);

  //handle logout
  const handleShowLogout = () => setShowLogout(true);
  const handleCloseLogout = () => setShowLogout(false);

  // Handle showing the reset password modal
  const handleShowResetPassword = () => {
    handleCloseLogin();
    setShowResetPassword(true);
  };

  const handleCloseresetPassword = () => setShowResetPassword(false);

  useEffect(() => {
    if (needsAuth !== props.isNotAuthenticated && props.isNotAuthenticated !== undefined) {
      setShowLogin(true);
      setNeedsAuth(props.isNotAuthenticated);
    }
  }, [props.isNotAuthenticated, needsAuth]);

  // Turn the NavBar black every time the user scrolls
  useEffect(() => {
    const handleScroll = () => turnBlackOnScrolling();

    window.addEventListener('scroll', handleScroll);

    if (props.scrolled) {
      turnNavBarBlack();
    }
    getUser();

    // Cleanup event listener on unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <div className="top-loading-bar" id="loading-bar"></div>
      <header
        className={
          scrolled
            ? classnames(style.header, 'trans_400', style.scrolled)
            : classnames(style.header, 'trans_400')
        }
      >
        <div className={style.logo}>
          <a href="/">
            <span>MER</span>maid
            <img src={logo} alt="" />
          </a>
        </div>
        <div className="container">
          <div className="row">
            <div className="col">
              <div
                className={classnames(
                  style.header_content,
                  'd-flex flex-row align-items-center justify-content-start trans_400'
                )}
              >
                <nav className={style.main_nav}>
                  <ul className="d-flex flex-row align-items-start justify-content-start">
                    <li>
                      <a href="/" className={props.active == 1 ? style.active : ''}>
                        Home
                      </a>
                    </li>
                    <li>
                      <a href="/queue" className={props.active == 2 ? style.active : ''}>
                        Queue
                      </a>
                    </li>
                    <li>
                      <a href="/browse" className={props.active == 3 ? style.active : ''}>
                        Browse
                      </a>
                    </li>
                    <li>
                      <a href="/about" className={props.active == 4 ? style.active : ''}>
                        About
                      </a>
                    </li>
                  </ul>
                </nav>
              </div>
            </div>
          </div>
        </div>

        {localStorage.getItem('token') ? (
          <div
            className={classnames(
              style.header_right,
              'd-flex flex-row align-items-start justify-content-start'
            )}
          >
            <div className={classnames(style.submit, style.signUp)}>
              <a onClick={handleShowLogout}>LogOut</a>
            </div>
            <div className={classnames('dropdown', style.userS)}>
              <button
                className={classnames('btn btn-secondary dropdown-toggle', style.d)}
                type="button"
                id="dropdownMenuButton"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
              >
                {user.name}
              </button>
              <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">

                <>
                  <a className="dropdown-item" onClick={handleShowRegister} href="#">
                    Create User
                  </a>
                  <a className="dropdown-item" href="/manage/users">
                    Manage Users
                  </a>
                </>
                ) : (
                <>
                  <a className="dropdown-item" href="/userprofile">
                    View Profile
                  </a>
                </>

              </div>
            </div>
            <div className={style.hamburger} onClick={hamburguerMenu}>
              <FontAwesomeIcon icon={faBars} aria-hidden="true" size="2x" inverse />
            </div>
          </div>
        ) : (
          <div
            className={classnames(
              style.header_right,
              'd-flex flex-row align-items-start justify-content-start'
            )}
          >
            <div className={classnames(style.submit, style.signUp)}>
              <a onClick={handleShowRegister}>SignUp</a>
            </div>
            <div className={classnames(style.submit, style.login)}>
              <a onClick={handleShowLogin}>Login</a>
            </div>
            <div className={style.hamburger} onClick={hamburguerMenu}>
              <FontAwesomeIcon icon={faBars} aria-hidden="true" size="2x" inverse />
            </div>
          </div>
        )}
      </header>

      <div className={activeMenu ? classnames(style.menu, style.active) : style.menu}>
        <div
          className={classnames(
            style.menu_content,
            'd-flex flex-column align-items-end justify-content-start'
          )}
        >
          <ul className={classnames(style.menu_nav_list, 'text-right')}>
            <li>
              <a href="/" className={props.active == 1 ? style.active : ''}>
                Home
              </a>
            </li>
            <li>
              <a href="/queue" className={props.active == 2 ? style.active : ''}>
                Queue
              </a>
            </li>
            <li>
              <a href="/browse" className={props.active == 3 ? style.active : ''}>
                Browse
              </a>
            </li>
            <li>
              <a href="/about" className={props.active == 4 ? style.active : ''}>
                About
              </a>
            </li>
          </ul>

          {localStorage.getItem('token') ? (
            <div
              className={classnames(
                style.menu_extra,
                'd-flex flex-column align-items-end justify-content-start'
              )}
            >
              <div className={style.menu_submit}>
                <a onClick={handleShowLogout} href="#">
                  LogOut
                </a>
              </div>
              <div className={classnames('dropdown', style.menu_submit2)}>
                <button
                  className={classnames('btn btn-secondary dropdown-toggle', style.d)}
                  type="button"
                  id="dropdownMenuButton"
                  data-toggle="dropdown"
                  aria-haspopup="true"
                  aria-expanded="false"
                >
                  {user.name}
                </button>
                <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                  {isAdmin ? (
                    <>
                      <a className="dropdown-item" onClick={handleShowRegister} href="#">
                        Create User
                      </a>
                      <a className="dropdown-item" href="/manage/users">
                        Manage Users
                      </a>
                    </>
                  ) : (
                    <>
                      <a className="dropdown-item" href="/userprofile">
                        View Profile
                      </a>
                    </>
                  )}
                </div>
              </div>
            </div>
          ) : (
            <div
              className={classnames(
                style.menu_extra,
                'd-flex flex-column align-items-end justify-content-start'
              )}
            >
              <div className={style.menu_submit}>
                <a onClick={handleShowLogin} href="#">
                  Login
                </a>
              </div>
              <div className={style.menu_submit}>
                <a onClick={handleShowRegister} href="#">SignUp</a>
              </div>
            </div>
          )}
        </div>
      </div>

      {/*Modal Register and Login */}

      {/*-----------------------------Register------------------- */}
      <Modal show={showRegister} onHide={handleCloseRegister}>
        <Modal.Header closeButton>
          <Modal.Title>{isAdmin ? 'Create User' : 'SignUp'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form className={style.signUpForm} onSubmit={Register}>
            <div id={style.errorMessage}></div>
            {isAdmin ? (
              <>
                <select className="form-select" aria-label="Default select example" id="isAdmin">
                  <option selected value={1}>
                    Normal User
                  </option>
                  <option value={2}>Admin</option>
                </select>
              </>
            ) : (
              <></>
            )}
            <label htmlFor="email">Name*</label>
            <input
              type={'text'}
              placeholder=""
              id="name"
              required
              onChange={(event) => setName(event.target.value)}
            ></input>

            <label htmlFor="email">Email*</label>
            <input
              type={'email'}
              placeholder="Enter your email..."
              required
              id="email"
              onChange={(event) => setEmail(event.target.value)}
            ></input>

            <label htmlFor="password">Password*</label>
            <input
              type={'password'}
              id="password"
              required
              onChange={(event) => setPassword(event.target.value)}
            ></input>

            <label htmlFor="Cpassword">Confirmar Password*</label>
            <input
              type={'password'}
              id="Cpassword"
              required
              onChange={(event) => setCPassword(event.target.value)}
            ></input>

            <button type="submit">{isAdmin ? 'Create User' : 'SignUp'}</button>
          </form>
        </Modal.Body>
      </Modal>
      {/*--------------------------LOGIN---------------------------- */}
      <Modal show={showLogin} onHide={handleCloseLogin}>
        <Modal.Header closeButton>
          <Modal.Title>Login</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form className={style.signUpForm} onSubmit={Login}>
            <div id={style.errorMessage2}></div>
            <label htmlFor="email">Email*</label>
            <input
              type={'email'}
              placeholder="Enter your email..."
              required
              id="email"
              onChange={(event) => setEmail(event.target.value)}
            ></input>

            <label htmlFor="password">Password*</label>
            <input
              type={'password'}
              id="password"
              required
              onChange={(event) => setPassword(event.target.value)}
            ></input>

            <button type="submit">Login</button>

            <a onClick={handleShowResetPassword} className={style.forgot}>
              <p>Forgot the Password ?</p>
            </a>
          </form>
        </Modal.Body>
      </Modal>

      {/* CONFIRM YOUR EMAIL */}
      <Modal show={showConfirmEmail} onHide={handleCloseConfirmEmail}>
        <Modal.Header closeButton>
          <Modal.Title>Email Confirmation</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className={style.emailConfirmation}>
            <div className={style.image}>
              <img src={emailLogo}></img>
            </div>
            <div className={style.text}>
              Thank you for registering! Before you can start using our service, please confirm your
              email address by clicking on the verification link that we&apos;ve sent to your inbox. If
              you don&apos;t see the email, please check your spam folder.
            </div>
          </div>
        </Modal.Body>
      </Modal>

      {/*LogOut */}
      <Modal show={showLogout} onHide={handleCloseLogout}>
        <Modal.Header closeButton>
          <Modal.Title>LogOut</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <div className="modal-body">Are you sure you want to log out?</div>
            <div className="modal-footer">
              <button type="button" className={style.LogOutButton} onClick={LogOut}>
                LogOut
              </button>
              <button
                type="button"
                className={style.CancelLogOutButton}
                onClick={handleCloseLogout}
              >
                Close
              </button>
            </div>
          </div>
        </Modal.Body>
      </Modal>

      {/*Reset Password */}
      <Modal show={showResetPassword} onHide={handleCloseresetPassword}>
        <Modal.Header closeButton>
          <Modal.Title>Reset Password</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div>
            <div className="modal-body">
              <form className={style.signUpForm} onSubmit={resetPassword}>
                <div id={style.errorMessage3}></div>
                <label htmlFor="email">Email*</label>
                <input
                  type={'email'}
                  placeholder="Enter your email..."
                  required
                  id="email"
                  onChange={(event) => setEmailReset(event.target.value)}
                ></input>
              </form>
            </div>
            <div className="modal-footer">
              <button type="button" className={style.LogOutButton} onClick={resetPassword}>
                Reset
              </button>
              <button
                type="button"
                className={style.CancelLogOutButton}
                onClick={handleCloseresetPassword}
              >
                Close
              </button>
            </div>
          </div>
        </Modal.Body>
      </Modal>
    </>
  );
}

export default NavBar;
